DROP TABLE IF EXISTS `page`;
CREATE TABLE `page` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` char(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `description` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `url` char(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `position` int(11) NOT NULL DEFAULT '0',
  `active` int(11) NOT NULL DEFAULT '1',
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `page_url_unique` (`url`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

INSERT INTO `page` (`id`, `title`, `description`, `url`, `date`, `position`, `active`, `created_at`, `updated_at`) VALUES
(1,	'LarrockCMS',	'<p><a href=\"https://github.com/Fanamurov/larrock-core\">LarrockCMS</a> - это CMS основанная на php-фреймворке Laravel поставляемая в формате composer-пакетов.</p>\r\n<p><a href=\"https://packagist.org/packages/fanamurov/larrock-core\"><img src=\"https://camo.githubusercontent.com/4995869bf897e3f0ecbada3a719f3ce06217c3a5/68747470733a2f2f706f7365722e707567782e6f72672f66616e616d75726f762f6c6172726f636b2d636f72652f762f737461626c65\" alt=\"Latest Stable Version\" data-canonical-src=\"https://poser.pugx.org/fanamurov/larrock-core/v/stable\" /></a> <a href=\"https://packagist.org/packages/fanamurov/larrock-core\"><img src=\"https://camo.githubusercontent.com/c5283dee214c79ef6003bda22e6a72d4994b38d0/68747470733a2f2f706f7365722e707567782e6f72672f66616e616d75726f762f6c6172726f636b2d636f72652f646f776e6c6f616473\" alt=\"Total Downloads\" data-canonical-src=\"https://poser.pugx.org/fanamurov/larrock-core/downloads\" /></a> <a href=\"https://packagist.org/packages/fanamurov/larrock-core\"><img src=\"https://camo.githubusercontent.com/a0c96d396406788440ec6194f9b11f179398df24/68747470733a2f2f706f7365722e707567782e6f72672f66616e616d75726f762f6c6172726f636b2d636f72652f6c6963656e7365\" alt=\"License\" data-canonical-src=\"https://poser.pugx.org/fanamurov/larrock-core/license\" /></a> <a href=\"https://www.codacy.com/app/Fanamurov/larrock-core?utm_source=github.com&amp;utm_medium=referral&amp;utm_content=Fanamurov/larrock-core&amp;utm_campaign=Badge_Grade\"><img src=\"https://camo.githubusercontent.com/e4bb7139f294082ede058b2ccaed640c2c7350c4/68747470733a2f2f6170692e636f646163792e636f6d2f70726f6a6563742f62616467652f47726164652f3161306662313966326530323436303761316434303236306338626161356537\" alt=\"Codacy Badge\" data-canonical-src=\"https://api.codacy.com/project/badge/Grade/1a0fb19f2e024607a1d40260c8baa5e7\" /></a></p>\r\n<p>Распространяется под лицензией LGPL. При использовании CMS вы обязаны указать ее название в своем проекте в виде копирайта в админ-панели.</p>\r\n<p>В основе проекта ядро CMS (LarrockCore), устанавливаемое в дополнение к laravel версии &gt;=5.5 и пакеты компонентов (дополнений к LarrockCore).</p>\r\n<p><strong>Внимание! LarrockCMS версии &gt;=0.2.x требует Mysql версии не ниже 5.7! Для работы с более старыми версиями используйте ветку 0.1.x.</strong></p>\r\n<p>&nbsp;</p>\r\n<p><a id=\"user-content-Компоненты-поставляемые-вместе-с-главным-пакетом-larrock-core\" class=\"anchor\" href=\"#%D0%9A%D0%BE%D0%BC%D0%BF%D0%BE%D0%BD%D0%B5%D0%BD%D1%82%D1%8B-%D0%BF%D0%BE%D1%81%D1%82%D0%B0%D0%B2%D0%BB%D1%8F%D0%B5%D0%BC%D1%8B%D0%B5-%D0%B2%D0%BC%D0%B5%D1%81%D1%82%D0%B5-%D1%81-%D0%B3%D0%BB%D0%B0%D0%B2%D0%BD%D1%8B%D0%BC-%D0%BF%D0%B0%D0%BA%D0%B5%D1%82%D0%BE%D0%BC-larrock-core\" aria-hidden=\"true\"></a><span class=\"uk-h2\">Компоненты поставляемые вместе с главным пакетом larrock-core:</span></p>\r\n<ul>\r\n<li><a href=\"https://packagist.org/packages/fanamurov/larrock-menu\"><img src=\"https://camo.githubusercontent.com/beb24057a13811a831beb952ee4f8ca5bdac88ac/68747470733a2f2f706f7365722e707567782e6f72672f66616e616d75726f762f6c6172726f636b2d6d656e752f762f737461626c65\" alt=\"Latest Stable Version\" data-canonical-src=\"https://poser.pugx.org/fanamurov/larrock-menu/v/stable\" /></a> <a href=\"https://github.com/Fanamurov/larrock-menu\">fanamurov/larrock-menu</a> - управление меню сайта</li>\r\n<li><a href=\"https://packagist.org/packages/fanamurov/larrock-users\"><img src=\"https://camo.githubusercontent.com/689c7c9efc86d9c5ebeaaac048a5b98cb7177eb8/68747470733a2f2f706f7365722e707567782e6f72672f66616e616d75726f762f6c6172726f636b2d75736572732f762f737461626c65\" alt=\"Latest Stable Version\" data-canonical-src=\"https://poser.pugx.org/fanamurov/larrock-users/v/stable\" /></a> <a href=\"https://github.com/Fanamurov/larrock-users\">fanamurov/larrock-users</a> - пользователи, права и роли</li>\r\n<li><a href=\"https://packagist.org/packages/fanamurov/larrock-pages\"><img src=\"https://camo.githubusercontent.com/c5f447d5cdaaa0fdf72e6b2f8e585676718c4f5a/68747470733a2f2f706f7365722e707567782e6f72672f66616e616d75726f762f6c6172726f636b2d70616765732f762f737461626c65\" alt=\"Latest Stable Version\" data-canonical-src=\"https://poser.pugx.org/fanamurov/larrock-pages/v/stable\" /></a> <a href=\"https://github.com/Fanamurov/larrock-pages\">fanamurov/larrock-pages</a> - страницы</li>\r\n<li><a href=\"https://packagist.org/packages/fanamurov/larrock-blocks\"><img src=\"https://camo.githubusercontent.com/f3726e2f069c4c4a4accae01740cb6b85aa6d359/68747470733a2f2f706f7365722e707567782e6f72672f66616e616d75726f762f6c6172726f636b2d626c6f636b732f762f737461626c65\" alt=\"Latest Stable Version\" data-canonical-src=\"https://poser.pugx.org/fanamurov/larrock-blocks/v/stable\" /></a> <a href=\"https://github.com/Fanamurov/larrock-blocks\">fanamurov/larrock-blocks</a> - блоки для вывода в шаблоне</li>\r\n<li><a href=\"https://packagist.org/packages/fanamurov/larrock-contact\"><img src=\"https://camo.githubusercontent.com/65564a4f8a0403dd6f41661977f92e796289142c/68747470733a2f2f706f7365722e707567782e6f72672f66616e616d75726f762f6c6172726f636b2d636f6e746163742f762f737461626c65\" alt=\"Latest Stable Version\" data-canonical-src=\"https://poser.pugx.org/fanamurov/larrock-contact/v/stable\" /></a> <a href=\"https://github.com/Fanamurov/larrock-contact\">fanamurov/larrock-contact</a> - вывод, обработка, отправка форм</li>\r\n<li><a href=\"https://packagist.org/packages/fanamurov/larrock-admin-seo\"><img src=\"https://camo.githubusercontent.com/dcf49c5656e769a1bcdc08ff233c5aa9defc8b14/68747470733a2f2f706f7365722e707567782e6f72672f66616e616d75726f762f6c6172726f636b2d61646d696e2d73656f2f762f737461626c65\" alt=\"Latest Stable Version\" data-canonical-src=\"https://poser.pugx.org/fanamurov/larrock-admin-seo/v/stable\" /></a> <a href=\"https://github.com/Fanamurov/larrock-admin-seo\">fanamurov/larrock-admin-seo</a> - управление seo-данными компонентов</li>\r\n<li><a href=\"https://packagist.org/packages/fanamurov/larrock-core\"><img src=\"https://poser.pugx.org/fanamurov/larrock-search/v/stable\" alt=\"Latest Stable Version\" width=\"81\" height=\"20\" data-canonical-src=\"https://poser.pugx.org/fanamurov/larrock-admin-search/v/stable\" /></a> <a href=\"https://github.com/Fanamurov/larrock-search\">fanamurov/larrock-search</a> - поиск по материалам компонентов в админке</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<p><a id=\"user-content-Другие-компоненты\" class=\"anchor\" href=\"#%D0%94%D1%80%D1%83%D0%B3%D0%B8%D0%B5-%D0%BA%D0%BE%D0%BC%D0%BF%D0%BE%D0%BD%D0%B5%D0%BD%D1%82%D1%8B\" aria-hidden=\"true\"></a><span class=\"uk-h2\">Другие компоненты:</span></p>\r\n<ul>\r\n<li><a href=\"https://packagist.org/packages/fanamurov/larrock-catalog\"><img src=\"https://camo.githubusercontent.com/8200a4f21cd1df3ed4cef869bbf4c8a8bc7a46b9/68747470733a2f2f706f7365722e707567782e6f72672f66616e616d75726f762f6c6172726f636b2d636174616c6f672f762f737461626c65\" alt=\"Latest Stable Version\" data-canonical-src=\"https://poser.pugx.org/fanamurov/larrock-catalog/v/stable\" /></a> <a href=\"https://github.com/Fanamurov/larrock-catalog\">fanamurov/larrock-catalog</a> - каталог товаров</li>\r\n<li><a href=\"https://packagist.org/packages/fanamurov/larrock-cart\"><img src=\"https://camo.githubusercontent.com/f144ce813012bb245b005be44049c999faf84376/68747470733a2f2f706f7365722e707567782e6f72672f66616e616d75726f762f6c6172726f636b2d636172742f762f737461626c65\" alt=\"Latest Stable Version\" data-canonical-src=\"https://poser.pugx.org/fanamurov/larrock-cart/v/stable\" /></a> <a href=\"https://github.com/Fanamurov/larrock-cart\">fanamurov/larrock-cart</a> - корзина покупок, сохранение заказов, оплаты</li>\r\n<li><a href=\"https://packagist.org/packages/fanamurov/larrock-discount\"><img src=\"https://camo.githubusercontent.com/689e453813c8af8211401a14fdf0582316953f56/68747470733a2f2f706f7365722e707567782e6f72672f66616e616d75726f762f6c6172726f636b2d646973636f756e742f762f737461626c65\" alt=\"Latest Stable Version\" data-canonical-src=\"https://poser.pugx.org/fanamurov/larrock-discount/v/stable\" /></a> <a href=\"https://github.com/Fanamurov/larrock-discount\">fanamurov/larrock-discount</a> - скидочная система к каталогу</li>\r\n<li><a href=\"https://packagist.org/packages/fanamurov/larrock-feed\"><img src=\"https://camo.githubusercontent.com/4d9b669bd449762126817948586561e9015446a7/68747470733a2f2f706f7365722e707567782e6f72672f66616e616d75726f762f6c6172726f636b2d666565642f762f737461626c65\" alt=\"Latest Stable Version\" data-canonical-src=\"https://poser.pugx.org/fanamurov/larrock-feed/v/stable\" /></a> <a href=\"https://github.com/Fanamurov/larrock-feed\">fanamurov/larrock-feed</a> - материалы в разделах</li>\r\n<li><a href=\"https://packagist.org/packages/fanamurov/larrock-reviews\"><img src=\"https://camo.githubusercontent.com/58c46484d70a0d99a2153c317690cf00d81a4cc5/68747470733a2f2f706f7365722e707567782e6f72672f66616e616d75726f762f6c6172726f636b2d726576696577732f762f737461626c65\" alt=\"Latest Stable Version\" data-canonical-src=\"https://poser.pugx.org/fanamurov/larrock-reviews/v/stable\" /></a> <a href=\"https://github.com/Fanamurov/larrock-reviews\">fanamurov/larrock-reviews</a> - отзывы, комментарии</li>\r\n<li><a href=\"https://packagist.org/packages/fanamurov/larrock-wizard\"><img src=\"https://camo.githubusercontent.com/9f661e0acf5197b3ea406f0ed1162508c3dbfa59/68747470733a2f2f706f7365722e707567782e6f72672f66616e616d75726f762f6c6172726f636b2d77697a6172642f762f737461626c65\" alt=\"Latest Stable Version\" data-canonical-src=\"https://poser.pugx.org/fanamurov/larrock-wizard/v/stable\" /></a> <a href=\"https://github.com/Fanamurov/larrock-wizard\">fanamurov/larrock-wizard</a> - импорт товаров в каталог через .xlsx-прайс</li>\r\n<li><a href=\"https://packagist.org/packages/fanamurov/larrock-smartbanners\"><img src=\"https://camo.githubusercontent.com/77c62693b64b11cd35551cdcc5e0f3e17f14a49f/68747470733a2f2f706f7365722e707567782e6f72672f66616e616d75726f762f6c6172726f636b2d736d61727462616e6e6572732f762f737461626c65\" alt=\"Latest Stable Version\" data-canonical-src=\"https://poser.pugx.org/fanamurov/larrock-smartbanners/v/stable\" /></a> <a href=\"https://github.com/Fanamurov/larrock-smartbanners\">fanamurov/larrock-smartbanners</a> - клиент баннерообменной сети</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<p><a id=\"user-content-Зависимости-компонентов\" class=\"anchor\" href=\"#%D0%97%D0%B0%D0%B2%D0%B8%D1%81%D0%B8%D0%BC%D0%BE%D1%81%D1%82%D0%B8-%D0%BA%D0%BE%D0%BC%D0%BF%D0%BE%D0%BD%D0%B5%D0%BD%D1%82%D0%BE%D0%B2\" aria-hidden=\"true\"></a><span class=\"uk-h2\">Зависимости компонентов</span></p>\r\n<ul>\r\n<li>\"php\": \"&gt;=7.0.0\",</li>\r\n<li>\"<a href=\"https://github.com/barryvdh/laravel-debugbar\">barryvdh/laravel-debugbar</a>\": \"~2.0\",</li>\r\n<li>\"<a href=\"https://github.com/barryvdh/laravel-ide-helper\">barryvdh/laravel-ide-helper</a>\": \"~2.1\",</li>\r\n<li>\"<a href=\"https://github.com/proengsoft/laravel-jsvalidation\">proengsoft/laravel-jsvalidation</a>\": \"^1.5\",</li>\r\n<li>\"<a href=\"https://github.com/albertcht/invisible-recaptcha\">albertcht/invisible-recaptcha</a>\": \"^1.7\",</li>\r\n<li>\"<a href=\"https://github.com/davejamesmiller/laravel-breadcrumbs\">davejamesmiller/laravel-breadcrumbs</a>\": \"^4.0\",</li>\r\n<li>\"<a href=\"https://github.com/nicolaslopezj/searchable\">nicolaslopezj/searchable</a>\": \"^1.9\",</li>\r\n<li>\"<a href=\"https://github.com/spatie/laravel-medialibrary\">spatie/laravel-medialibrary</a>\": \"^6.0.0\",</li>\r\n<li>\"<a href=\"https://github.com/kix/mdash-bundle\">kix/mdash</a>\": \"^0.5.4\"</li>\r\n</ul>',	'larrockcms',	'2017-10-31 14:00:00',	0,	1,	'2017-10-31 16:17:25',	'2017-10-31 16:30:46');

DROP TABLE IF EXISTS `password_resets`;
CREATE TABLE `password_resets` (
  `email` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `token` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  KEY `password_resets_email_index` (`email`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;


DROP TABLE IF EXISTS `permissions`;
CREATE TABLE `permissions` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `slug` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `description` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `model` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `permissions_slug_unique` (`slug`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;


DROP TABLE IF EXISTS `permission_role`;
CREATE TABLE `permission_role` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `permission_id` int(10) unsigned NOT NULL,
  `role_id` int(10) unsigned NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `permission_role_permission_id_index` (`permission_id`),
  KEY `permission_role_role_id_index` (`role_id`),
  CONSTRAINT `permission_role_permission_id_foreign` FOREIGN KEY (`permission_id`) REFERENCES `permissions` (`id`) ON DELETE CASCADE,
  CONSTRAINT `permission_role_role_id_foreign` FOREIGN KEY (`role_id`) REFERENCES `roles` (`id`) ON DELETE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;


DROP TABLE IF EXISTS `permission_user`;
CREATE TABLE `permission_user` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `permission_id` int(10) unsigned NOT NULL,
  `user_id` int(10) unsigned NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `permission_user_permission_id_index` (`permission_id`),
  KEY `permission_user_user_id_index` (`user_id`),
  CONSTRAINT `permission_user_permission_id_foreign` FOREIGN KEY (`permission_id`) REFERENCES `permissions` (`id`) ON DELETE CASCADE,
  CONSTRAINT `permission_user_user_id_foreign` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;


DROP TABLE IF EXISTS `roles`;
CREATE TABLE `roles` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `slug` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `description` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `level` int(11) NOT NULL DEFAULT '1',
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `roles_slug_unique` (`slug`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

INSERT INTO `roles` (`id`, `name`, `slug`, `description`, `level`, `created_at`, `updated_at`) VALUES
(1,	'Admin',	'Админ',	NULL,	3,	NULL,	NULL),
(2,	'Moderator',	'Модератор',	NULL,	2,	NULL,	NULL),
(3,	'User',	'Пользователь',	NULL,	1,	NULL,	NULL);

DROP TABLE IF EXISTS `role_user`;
CREATE TABLE `role_user` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `role_id` int(10) unsigned NOT NULL,
  `user_id` int(10) unsigned NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `role_user_role_id_index` (`role_id`),
  KEY `role_user_user_id_index` (`user_id`),
  CONSTRAINT `role_user_role_id_foreign` FOREIGN KEY (`role_id`) REFERENCES `roles` (`id`) ON DELETE CASCADE,
  CONSTRAINT `role_user_user_id_foreign` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

INSERT INTO `role_user` (`id`, `role_id`, `user_id`, `created_at`, `updated_at`) VALUES
(1,	1,	1,	'2017-10-23 19:33:56',	'2017-10-23 19:33:56');

DROP TABLE IF EXISTS `seo`;
CREATE TABLE `seo` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `seo_title` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `seo_description` text COLLATE utf8mb4_unicode_ci,
  `seo_keywords` text COLLATE utf8mb4_unicode_ci,
  `seo_id_connect` int(11) DEFAULT NULL,
  `seo_url_connect` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `seo_type_connect` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;


DROP TABLE IF EXISTS `users`;
CREATE TABLE `users` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `email` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `password` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `remember_token` varchar(100) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  `first_name` char(255) COLLATE utf8mb4_unicode_ci DEFAULT '',
  `last_name` char(255) COLLATE utf8mb4_unicode_ci DEFAULT '',
  `fio` char(255) COLLATE utf8mb4_unicode_ci DEFAULT '',
  `address` char(255) COLLATE utf8mb4_unicode_ci DEFAULT '',
  `tel` char(255) COLLATE utf8mb4_unicode_ci DEFAULT '',
  `permissions` char(255) COLLATE utf8mb4_unicode_ci DEFAULT '',
  PRIMARY KEY (`id`),
  UNIQUE KEY `users_email_unique` (`email`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

INSERT INTO `users` (`id`, `name`, `email`, `password`, `remember_token`, `created_at`, `updated_at`, `first_name`, `last_name`, `fio`, `address`, `tel`, `permissions`) VALUES
(1,	'Admin',	'admin@larrock-cms.ru',	'$2y$10$qlIjdkRQXPBuQR5F0Lzs.uv42BGTVOHhPPI.YaDTgUfHTnpsRyHeu',	NULL,	'2017-10-23 19:33:56',	'2017-10-23 19:33:56',	'Admin',	'Khabarovsk',	'Admin Khabarovsk',	'',	'',	'');

